import "./App.css";
import React, { useState } from "react";

const App = () => {
  const url = "https://api.quotable.io/random";
  let quoteData = {
    content: "Let time be your only competitor.",
    author: "Ahmed Saber",
  };
  const [quote, setQuote] = useState(quoteData);

  const generateQuote = () => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setQuote(data);
      });
  };

  const copy = () => {
    navigator.clipboard.writeText(
      quote.author + " once said: " + quote.content
    );
    alert("copied");
  };

  const shareOnWhatsApp = () => {
    const text = quote.author + " once said: " + quote.content;
    const url = "whatsapp://send?text=" + encodeURIComponent(text);
    window.open(url);
  };

  const shareOnTwitter = () => {
    const text = quote.author + " once said: " + quote.content;
    const url =
      "https://twitter.com/intent/tweet?text=" + encodeURIComponent(text);
    window.open(url);
  };

  const shareOnFacebook = () => {
    const url =
      "https://www.facebook.com/sharer/sharer.php?u=" +
      encodeURIComponent(window.location.href);
    window.open(url);
  };

  const shareOnReddit = () => {
    const title = "Quote by " + quote.author;
    const url = window.location.href;
    const redditUrl =
      "https://www.reddit.com/submit?title=" +
      encodeURIComponent(title) +
      "&url=" +
      encodeURIComponent(url);
    window.open(redditUrl);
  };

  return (
    <>
      <h1>Quote Generator React App</h1>
      <div className="container">
        <p>{quote.content}</p>
        <span>{quote.author}</span>
        <div className="btns">
          <button onClick={copy} className="btn">
            Copy
          </button>
          <button onClick={generateQuote}>Generate Another Quote</button>
        </div>
        <button onClick={shareOnWhatsApp}>WhatsApp</button>
        <button onClick={shareOnTwitter}>Twitter</button>
        <button onClick={shareOnFacebook}>Facebook</button>
      </div>
    </>
  );
};

export default App;
